class Client_info extends CI_Controller {

    function __construct() {
        parent::__construct();
        $this->load->model(array('Client_info_model'));
        $this->load->helper(array('datatable'));
        $this->load->library(array('form_validation', 'PostData'));
    }

    /*
     * Listing of client_info
     */

    function index() {
        $data['_view'] = 'client_info/index';
        $this->load->view('layouts/main', $data);
    }

    /**
     * Function to get all clients
     * 
     * @return json result
     */
    function getAllClients() {
        $request = $_GET;
        $clientsResult = $this->Client_info_model->getAllClientsData($request, array());
        echo json_encode($clientsResult);
        exit;
    }

    /*
     * Adding a new client_info
     */

    function add() {
        $postData = $this->input->post(NULL, TRUE);
        if (!empty($postData)) {
            if ($this->form_validation->run("client_info_add_form") == TRUE) {
                $client_post_data = $this->postdata->getClientPost($postData);
                $client_info_id = $this->Client_info_model->add_client_info($postData);
                redirect("client_info/index");
            } else {
                $data['_view'] = 'client_info/add';
                $this->load->view('layouts/main', $data);
            }
        } else {
            $data['_view'] = 'client_info/add';
            $this->load->view('layouts/main', $data);
        }
    }

    /*
     * Editing a client_info
     */

    function edit($CLIENT_ID) {
        $postData = $this->input->post(NULL, TRUE);
        $data['client_info'] = $this->Client_info_model->get_client_info($CLIENT_ID);
        if (isset($postData) && !empty($postData)) {
            if ($this->form_validation->run("client_info_edit_form") == TRUE) {
                $this->Client_info_model->update_client_info($CLIENT_ID, $postData);
                redirect('client_info/index');
            } else {
                $data['_view'] = 'client_info/edit';
                $this->load->view('layouts/main', $data);
            }
        } else {
            $data['_view'] = 'client_info/edit';
            $this->load->view('layouts/main', $data);
        }
    }

    /*
     * Deleting client_info
     */

    function remove($CLIENT_ID) {
        $client_info = $this->Client_info_model->get_client_info($CLIENT_ID);

        // check if the client_info exists before trying to delete it
        if (isset($client_info['CLIENT_ID'])) {
            $this->Client_info_model->delete_client_info($CLIENT_ID);
            redirect('client_info/index');
        } else
            show_error('The client_info you are trying to delete does not exist.');
    }

}
