class Masters_cash_management extends CI_Controller {

    function __construct() {
        parent::__construct();
        $this->load->model('Masters_cash_management_model');
        $this->load->model('Client_info_model');
    }

    /*
     * Listing of masters_cash_management
     */

    function index() {
        $data['masters_cash_management'] = $this->Masters_cash_management_model->get_all_masters_cash_management();

        $data['_view'] = 'masters_cash_management/index';
        $this->load->view('layouts/main', $data);
    }

    /**
     * Function to get all masters cash management data
     * 
     * @return json result
     */
    function getAllMastersCashManagement() {
        $request = $_GET;
        $result = $this->Masters_cash_management_model->getAllMastersCashManagementData($request, array());
        echo json_encode($result);
        exit;
    }

    /*
     * Adding a new masters_cash_management
     */

    function add() {
        if (isset($_POST) && count($_POST) > 0) {
            $params = array(
                'CASHMANAGEMENT_DATE' => $this->input->post('CASHMANAGEMENT_DATE'),
                'CASHMANAGEMENT_AMOUNT' => $this->input->post('CASHMANAGEMENT_AMOUNT'),
                'CASHMANAGEMENT_RETURN_AMOUNT' => $this->input->post('CASHMANAGEMENT_RETURN_AMOUNT'),
                'CREATED_DATE' => date("Y-m-d H:i:s"),
                'CREATED_BY' => $this->session->userdata['user']['LOGIN_ID'],
                'CLIENT_ID' => $this->input->post('CLIENT_ID'),
                'IS_ACTIVE' => 1
            );

            $masters_cash_management_id = $this->Masters_cash_management_model->add_masters_cash_management($params);
            redirect('masters_cash_management/index');
        } else {
            $data['all_client_info'] = $this->Client_info_model->get_all_client_info();
            $data['_view'] = 'masters_cash_management/add';
            $this->load->view('layouts/main', $data);
        }
    }

    /*
     * Editing a masters_cash_management
     */

    function edit($CASHMANAGEMENT_ID) {
        // check if the masters_cash_management exists before trying to edit it
        $data['masters_cash_management'] = $this->Masters_cash_management_model->get_masters_cash_management($CASHMANAGEMENT_ID);

        if (isset($data['masters_cash_management']['CASHMANAGEMENT_ID'])) {
            if (isset($_POST) && count($_POST) > 0) {
                $params = array(
                    'CASHMANAGEMENT_DATE' => $this->input->post('CASHMANAGEMENT_DATE'),
                    'CASHMANAGEMENT_AMOUNT' => $this->input->post('CASHMANAGEMENT_AMOUNT'),
                    'CASHMANAGEMENT_RETURN_AMOUNT' => $this->input->post('CASHMANAGEMENT_RETURN_AMOUNT'),
                    'MODIFIED_DATE' => date("Y-m-d H:i:s"),
                    'MODIFIED_BY' => $this->session->userdata['user']['LOGIN_ID'],
                    'CLIENT_ID' => $this->input->post('CLIENT_ID'),
                    'IS_ACTIVE' => 1,
                );

                $this->Masters_cash_management_model->update_masters_cash_management($CASHMANAGEMENT_ID, $params);
                redirect('masters_cash_management/index');
            } else {
                $data['all_client_info'] = $this->Client_info_model->get_all_client_info();
                $data['_view'] = 'masters_cash_management/edit';
                $this->load->view('layouts/main', $data);
            }
        } else
            show_error('The masters_cash_management you are trying to edit does not exist.');
    }

    /*
     * Deleting masters_cash_management
     */

    function remove($CASHMANAGEMENT_ID) {
        $masters_cash_management = $this->Masters_cash_management_model->get_masters_cash_management($CASHMANAGEMENT_ID);

        // check if the masters_cash_management exists before trying to delete it
        if (isset($masters_cash_management['CASHMANAGEMENT_ID'])) {
            $this->Masters_cash_management_model->delete_masters_cash_management($CASHMANAGEMENT_ID);
            redirect('masters_cash_management/index');
        } else
            show_error('The masters_cash_management you are trying to delete does not exist.');
    }

}
