    /*
     * Listing of masters_orgtype
     */

    function index() {
        $data['masters_orgtype'] = $this->Masters_orgtype_model->get_all_masters_orgtype();

        $data['_view'] = 'masters_orgtype/index';
        $this->load->view('layouts/main', $data);
