class Loan_info extends CI_Controller {

    function __construct() {
        parent::__construct();
        $this->load->model('Loan_info_model');
        $session_data = (isset($this->session->userdata['EMP_DATA']) && !empty($this->session->userdata['EMP_DATA'])) ? $this->session->userdata['EMP_DATA'] : array();
        if (empty($session_data))
            redirect("login");
    }

    /*
     * Listing of client_info
     */

    public function index() {
        $data['loan_info'] = $this->Loan_info_model->get_all_loan_info();

        $data['_view'] = 'loan_info/index';
        $this->load->view('layouts/main', $data);
    }

