class Loan_model extends CI_Model
{
    function __construct()
    {
        parent::__construct();
    }
    
    /*
     * Get loan by loan_id
     */
    function get_loan($loan_id)
    {
        return $this->db->get_where('Loans',array('loan_id'=>$loan_id))->row_array();
    }
    
     /*
     * Get loan by customer_id
     */
    function getLoanByCustomerId($customer_id)
    {
        return $this->db->get_where('Loans',array('customer_id'=>$customer_id))->result_array();
    }

    /*
     * Get all loans
     */
    function get_all_loans()
    {
        return $this->db->get('Loans')->result_array();
    }
    
    /*
     * function to add new loan
     */
    function add_loan($params)
    {
        $this->db->insert('Loans',$params);
        return $this->db->insert_id();
    }
    
    /*
     * function to update loan
     */
    function update_loan($loan_id,$params)
    {
        $this->db->where('loan_id',$loan_id);
        $response = $this->db->update('Loans',$params);
        if($response)
        {
            return "loan updated successfully";
        }
        else
        {
            return "Error occuring while updating loan";
        }
    }
    
    /*
     * function to delete loan
     */
    function delete_loan($loan_id)
    {
        $response = $this->db->delete('Loans',array('loan_id'=>$loan_id));
        if($response)
        {
            return "loan deleted successfully";
        }
        else
        {
            return "Error occuring while deleting loan";
        }
    }
}
