class Module_model extends CI_Model
{
    function __construct()
    {
        parent::__construct();
    }
    
    /*
     * Get module by MODULE_ID
     */
    function get_module($MODULE_ID)
    {
        return $this->db->get_where('modules',array('MODULE_ID'=>$MODULE_ID))->row_array();
    }
    
    /*
     * Get all modules
     */
    function get_all_modules()
    {
        return $this->db->get('modules')->result_array();
    }
    
    /*
     * function to add new module
     */
    function add_module($params)
    {
        $this->db->insert('modules',$params);
        return $this->db->insert_id();
    }
    
    /*
     * function to update module
     */
    function update_module($MODULE_ID,$params)
    {
        $this->db->where('MODULE_ID',$MODULE_ID);
        $response = $this->db->update('modules',$params);
        if($response)
        {
            return "module updated successfully";
        }
        else
        {
            return "Error occuring while updating module";
        }
    }
    
    /*
     * function to delete module
     */
    function delete_module($MODULE_ID)
    {
        $response = $this->db->delete('modules',array('MODULE_ID'=>$MODULE_ID));
        if($response)
        {
            return "module deleted successfully";
        }
        else
        {
            return "Error occuring while deleting module";
        }
    }
}
